var link=document.querySelector(".feedback-button"),popup=document.querySelector(".modal"),close=popup.querySelector(".close-button"),userName=popup.querySelector("[name=user-name]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),userName.focus(),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")})}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&popup.classList.remove("modal-show"))});